import {isNode} from '../utils/node';
import DataStoreContract from './DataStoreContract';

/**
 * @private
 */
export default class FileDriver extends DataStoreContract {
  /**
   * @param {String} path - File storage path
   * @inheritDoc
   */
  constructor(path = '.m4n') {
    super();

    this._path = path;
  }

  /**
   * File storage path
   * @returns {String} - path
   */
  get path() {
    return this._path;
  }

  /**
   * File storage path
   * @param {String} value - path
   */
  set path(value) {
    if (typeof value !== 'string') {
      throw new TypeError('Expected "path" value to be of type "string"');
    }

    this._path = value;
  }

  get realPath() {
    if (this.path.startsWith('/')) {
      return this.path;
    }

    // eslint-disable-next-line no-undef
    return this._fs.realpathSync(this.path);
  }

  /**
   * @inheritDoc
   */
  static get available() {
    return isNode();
  }

  /**
   * @inheritDoc
   */
  static get secure() {
    return true;
  }

  /**
   * @inheritDoc
   */
  set(name, value) {
    const data = this._read();

    data[name] = value;

    this._write(data);
  }

  /**
   * @inheritDoc
   */
  get(name) {
    return this._read()[name];
  }

  /**
   * @inheritDoc
   */
  remove(name) {
    const data = this._read();

    delete data[name];

    this._write(data);
  }

  /**
   * @inheritDoc
   */
  clear() {
    this._write({});
  }

  /**
   * @inheritDoc
   */
  keys() {
    return Object.keys(this._read());
  }

  /**
   * Read file and parse
   * @returns {Object<String, String>} - Key, value object
   * @private
   */
  _read() {
    let data;

    try {
      data = this._fs.readFileSync(this.realPath).toString();
    } catch (e) {
      data = '{}';
    }

    if (!data) {
      return {};
    }

    return JSON.parse(data);
  }

  /**
   * Write data to file
   * @param {Object<String, String>} obj - Key, value object
   * @returns {void}
   * @private
   */
  _write(obj) {
    const data = JSON.stringify(obj);
    const fd = this._fs.openSync(this.realPath, 'w');

    this._fs.writeSync(fd, data);
    this._fs.closeSync(fd);
  }

  /**
   * Get fs instance
   * @returns {fs} - fs instance
   * @private
   */
  get _fs() {
    if (!this.__fs) {
      // eslint-disable-next-line no-eval
      this.__fs = eval('require("fs")');
    }

    return this.__fs;
  }
}

