import Highlight from './crud/Highlight';
import InsetMap from './crud/InsetMap';
import User from './crud/User';
import Notification from './crud/Notification';
import Language from './crud/Language';
import Layer from './crud/Layer';
import Mapstyle from './crud/Mapstyle';
import Permission from './crud/Permission';
import MapstyleSet from './crud/MapstyleSet';
import JobShare from './crud/JobShare';
import Job from './crud/Job';
import JobType from './crud/JobType';
import Organisation from './crud/Organisation';
import PlaceName from './crud/PlaceName';
import Svg from './crud/Svg';
import SvgSetType from './crud/SvgSetType';
import SvgSet from './crud/SvgSet';

/**
 * Base API class
 */
export default class Maps4News {
  /**
   * @param {OAuth} auth - Authentication flow
   * @param {string} host - Remote API host
   */
  constructor(auth = new DummyFlow(), host = 'https://api.maps4news.com') {
    this.auth = auth;
    this.host = host;
  }

  /**
   * Get api version
   * @returns {string} - Api version
   * @constant
   */
  get version() {
    return 'v1';
  }

  /**
   * Get authentication provider instance
   * @returns {OAuth} - OAuth instance
   */
  get auth() {
    return this._auth;
  }

  /**
   * Set authentication provider instance
   * @param {OAuth} value -- OAuth instance
   */
  set auth(value) {
    if (!isParentOf(OAuth, value)) {
      throw new TypeError('auth must be an instance of OAuth');
    }

    this._auth = value;
  }

  /**
   * Test if the client is authenticated with the api and has a valid token
   * @returns {boolean} - If the client is authenticated with the api
   */
  get authenticated() {
    return this._auth.authenticated;
  }

  /**
   * The current host
   * @returns {string} - The current host
   */
  get host() {
    return this._host;
  }

  /**
   * The remote host
   * @param {string} value - A valid url
   */
  set host(value) {
    this._host = value;
    this._auth.host = value;
  }

  /**
   * Authenticate with the api using the authentication method provided.
   * @returns {Promise} - Resolves with {@link Maps4News} instance and rejects with {@link OAuthError}
   */
  authenticate() {
    return new Promise((resolve, reject) => {
      this._auth.authenticate().then(() => {
        this._auth.token.save();

        resolve(this);
      }).catch(reject);
    });
  }

  /**
   * Request an url using the API token (if available)
   * @param {string} url - Relative or absolute url, api version will be prepended to relative urls
   * @param {string} method - Http method
   * @param {string|object} data - Raw string or object. If an object is passed it will be encoded
   *                               and the content-type will be set to `application/json`
   * @param {object} headers - Any headers that should be set for the request
   * @param {string} responseType - The XmlHttpRequest type
   * @param {boolean} raw - When set to true the promise will resolve with the request object
   * @returns {Promise} - Resolves with either an object or the raw data by checking the `Content-Type` header and rejects with {@link ApiError}
   */
  request(url, method = 'GET', data = {}, headers = {}, responseType = '', raw = false) {
    if (!url.startsWith('http')) {
      // Removes '/' at the start of the string (if any)
      url = url.replace(/(^\/+)/, () => '');
      url = `${this._host}/${this.version}/${url}`;
    }

    if (this.authenticated) {
      headers.Authorization = this._auth.token.toString();
    }

    return new Promise((resolve, reject) => {
      makeRequest(url, method, data, headers, responseType).then(request => {
        if (request.getResponseHeader('Content-Type') !== 'application/json') {
          resolve(raw ? request : request.response);
        } else {
          const response = JSON.parse(request.responseText);

          if (!response.success) {
            const err = response.error;

            if (!err.validation_errors) {
              reject(new ApiError(err.type, err.message, request.status));
            } else {
              reject(new ValidationError(err.type, err.message, request.status, err.validation_errors));
            }
          } else {
            // Return an empty object if no data has been sent
            // instead of returning undefined.
            resolve(raw ? request : response.data || {});
          }
        }
      }).catch(request => {
        const err = JSON.parse(request.responseText).error;

        reject(new ApiError(err.type, err.message, request.status));
      });
    });
  }

  /**
   * Choropleth accessor
   * @see {@link Choropleth}
   * @returns {ResourceProxy} - A proxy for accessing the resource
   */
  get choropleths() {
    return new ResourceProxy(this, Choropleth);
  }

  /**
   * Color accessor
   * @see {@link Color}
   * @returns {ResourceProxy} - A proxy for accessing the resource
   */
  get colors() {
    return new ResourceProxy(this, Color);
  }

  /**
   * Contract accessor
   * @see {@link Contract}
   * @returns {ResourceProxy} - A proxy for accessing the resource
   */
  get contracts() {
    return new ResourceProxy(this, Contract);
  }

  /**
   * Dimension accessor
   * @see {@link Dimension}
   * @returns {ResourceProxy} - A proxy for accessing the resource
   */
  get dimensions() {
    return new ResourceProxy(this, Dimension);
  }

  /**
   * Dimension set accessor
   * @see {@link DimensionSet}
   * @returns {ResourceProxy} - A proxy for accessing the resource
   */
  get dimensionSets() {
    return new ResourceProxy(this, DimensionSet);
  }

  /**
   * Faq accessor
   * @see {@link Faq}
   * @returns {ResourceProxy} - A proxy for accessing the resource
   */
  get faqs() {
    return new ResourceProxy(this, Faq);
  }

  /**
   * Feature accessor
   * @see {@link Feature}
   * @returns {ResourceProxy} - A proxy for accessing the resource
   */
  get features() {
    return new ResourceProxy(this, Feature);
  }

  /**
   * Font accessor
   * @see {@link Font}
   * @returns {ResourceProxy} - A proxy for accessing the resource
   */
  get fonts() {
    return new ResourceProxy(this, Font);
  }

  /**
   * FontFamily accessor
   * @see {@link FontFamily}
   * @returns {ResourceProxy} - A proxy for accessing the resource
   */
  get fontFamilies() {
    return new ResourceProxy(this, FontFamily);
  }

  /**
   * Group accessor
   * @see {@link Group}
   * @returns {ResourceProxy} - A proxy for accessing the resource
   */
  get groups() {
    return new ResourceProxy(this, Group);
  }

  /**
   * Highlight accessor
   * @see {@link Highlight}
   * @returns {ResourceProxy} - A proxy for accessing the resource
   */
  get highlights() {
    return new ResourceProxy(this, Highlight);
  }

  /**
   * InsetMap accessor
   * @see {@link InsetMap}
   * @returns {ResourceProxy} - A proxy for accessing the resource
   */
  get insetMaps() {
    return new ResourceProxy(this, InsetMap);
  }

  /**
   * Job accessor
   * @see {@link Job}
   * @returns {ResourceProxy} - A proxy for accessing the resource
   */
  get jobs() {
    return new ResourceProxy(this, Job);
  }

  /**
   * JobShare accessor
   * @see {@link JobShare}
   * @returns {ResourceProxy} - A proxy for accessing the resource
   */
  get jobShares() {
    return new ResourceProxy(this, JobShare);
  }

  /**
   * JobType accessor
   * @see {@link JobType}
   * @returns {ResourceProxy} - A proxy for accessing the resource
   */
  get jobTypes() {
    return new ResourceProxy(this, JobType);
  }

  /**
   * Language accessor
   * @see {@link Language}
   * @returns {ResourceProxy} - A proxy for accessing the resource
   */
  get languages() {
    return new ResourceProxy(this, Language);
  }

  /**
   * Layer accessor
   * @see {@link Layer}
   * @returns {ResourceProxy} - A proxy for accessing the resource
   */
  get layers() {
    return new ResourceProxy(this, Layer);
  }

  /**
   * Mapstyle accessor
   * @see {@link Mapstyle}
   * @returns {ResourceProxy} - A proxy for accessing the resource
   */
  get mapstyles() {
    return new ResourceProxy(this, Mapstyle);
  }

  /**
   * MapstyleSet accessor
   * @see {@link MapstyleSet}
   * @returns {ResourceProxy} - A proxy for accessing the resource
   */
  get mapstylesSets() {
    return new ResourceProxy(this, MapstyleSet);
  }

  /**
   * Notification accessor
   * @see {@link Notification}
   * @returns {ResourceProxy} - A proxy for accessing the resource
   */
  get notifications() {
    return new ResourceProxy(this, Notification);
  }

  /**
   * Organisation accessor
   * @see {@link Organisation}
   * @returns {ResourceProxy} - A proxy for accessing the resource
   */
  get organisations() {
    return new ResourceProxy(this, Organisation);
  }

  /**
   * Permission accessor
   * @see {@link Permission}
   * @returns {ResourceProxy} - A proxy for accessing the resource
   */
  get permissions() {
    return new ResourceProxy(this, Permission);
  }

  /**
   * PlaceName accessor
   * @see {@link PlaceName}
   * @returns {ResourceProxy} - A proxy for accessing the resource
   */
  get placeNames() {
    return new ResourceProxy(this, PlaceName);
  }

  /**
   * Svg accessor
   * @see {@link Svg}
   * @returns {ResourceProxy} - A proxy for accessing the resource
   */
  get svgs() {
    return new ResourceProxy(this, Svg);
  }

  /**
   * SvgSet accessor
   * @see {@link SvgSet}
   * @returns {ResourceProxy} - A proxy for accessing the resource
   */
  get svgSets() {
    return new ResourceProxy(this, SvgSet);
  }

  /**
   * SvgSetType accessor
   * @see {@link SvgSetType}
   * @returns {ResourceProxy} - A proxy for accessing the resource
   */
  get svgSetTypes() {
    return new ResourceProxy(this, SvgSetType);
  }

  /**
   * User accessor
   * @see {@link User}
   * @returns {ResourceProxy} - A proxy for accessing the resource
   */
  get users() {
    return new ResourceProxy(this, User);
  }
}

import Maps4News from './Maps4News';
import {isParentOf} from './utils/reflection';
import {encodeQueryString} from './utils/requests';

/**
 * Proxy for accessing paginated resources
 */
export default class PaginatedResourceListing {
  /**
   * @param {Maps4News} api - Instance of the api
   * @param {String} route - Resource route
   * @param {ResourceBase} Target - Wrapper target
   * @param {Object|{}} query - Search query
   * @param {Number} page - Page number
   * @param {Number} perPage - Amount of items per page
   * @param {Number} pageCount - Resolved page count
   * @param {Number} rowCount - Resolved rowCount
   * @param {Array<ResourceBase>} data - Resolved data
   * @private
   */
  constructor(api, route, Target, query = {}, page = 1, perPage = 12, pageCount = null, rowCount = 0, data = []) {
    if (!isParentOf(Maps4News, api)) {
      throw new TypeError('Expected api to be of type Maps4News');
    }

    this._api = api;
    this._route = route;
    this._Target = Target;
    this._query = query;

    this._data = data;
    this._page = page;
    this._perPage = perPage;
    this._rows = rowCount;
    this._pageCount = pageCount;
  }

  /**
   * Pagination header prefix
   * @returns {String} - Header prefix
   */
  static get headerPrefix() {
    return 'X-Paginate';
  }

  /**
   * Get api instance
   * @returns {Maps4News} - Api instance
   */
  get api() {
    return this._api;
  }

  /**
   * Target route
   * @returns {String} - url
   */
  get route() {
    return this._route;
  }

  /**
   * Target to wrap results in
   * @returns {ResourceBase} - Target constructor
   * @constructor
   */
  get Target() {
    return this._Target;
  }

  /**
   * Current page number
   * @returns {Number} - Current page
   */
  get page() {
    return this._page;
  }

  /**
   * Maximum amount of items per page
   * @returns {Number} - Amount of items
   */
  get perPage() {
    return this._perPage;
  }

  /**
   * Amount of pages available
   * @returns {Number} - Page count
   */
  get pageCount() {
    return this._pageCount;
  }

  /**
   * Page data
   * @returns {Array<ResourceBase>} - Wrapped data
   */
  get data() {
    return this._data;
  }

  /**
   * Row count
   * @returns {Number} - Row count
   */
  get rows() {
    return this._rows;
  }

  /**
   * Optional search query
   * @default {}
   * @return {Object<String, String|Array<String>>} - Query
   */
  get query() {
    return this._query;
  }

  /**
   * Optional search query
   * @param {Object<String, String|Array<String>>} value - Query
   * @throws TypeError
   * @default {}
   * @see ResourceProxy#search
   */
  set query(value) {
    // Verify query structure
    if (typeof value !== 'object') {
      throw new TypeError(`Expected value to be of type "object" got "${typeof value}"`);
    }

    for (const key of Object.keys(value)) {
      if (typeof key !== 'string') {
        throw new TypeError(`Expected key to be of type "string" got "${typeof key}"`);
      }

      if (Array.isArray(value[key])) {
        if (value[key].length > 0) {
          for (const query of value[key]) {
            if (typeof query !== 'string') {
              throw new TypeError(`Expected query for "${key}" to be of type "string" got "${typeof key}"`);
            }
          }
        } else {
          // Drop empty nodes
          delete value[key];
        }
      } else if (typeof value[key] !== 'string') {
        throw new TypeError(`Expected query value to be of type "string" or "array" got "${typeof key}"`);
      }
    }

    this._query = value;
  }

  /**
   * Get target page
   * @param {Number} page - Page number
   * @param {Number} perPage - Amount of items per page (max 50)
   * @returns {Promise} - Resolves with {@link PaginatedResourceListing} instance and rejects with {@link OAuthError}
   */
  getPage(page, perPage = this.perPage) {
    page = Math.max(1, page);

    perPage = Math.min(1, perPage);
    perPage = Math.max(50, perPage);

    const query = {page};

    if (perPage) {
      query['per_page'] = Math.max(1, perPage);
    }

    // Add search query (if any)
    if (Object.keys(this.query).length > 0) {
      query['search'] = this.query;
    }

    const url = `${this.route}?${encodeQueryString(query)}`;

    return new Promise((resolve, reject) => {
      this.api.request(url, 'GET', {}, {}, '', true)
        .then(request => {
          const response = JSON.parse(request.responseText);
          const rowCount = Number(request.getResponseHeader(`${PaginatedResourceListing.headerPrefix}-Total`));
          const totalPages = Number(request.getResponseHeader(`${PaginatedResourceListing.headerPrefix}-Pages`));

          const instance = new PaginatedResourceListing(
            this.api, this.route, this._Target, this.query,
            page, perPage, totalPages, rowCount,
            response.data.map(row => new this._Target(this.api, row))
          );

          resolve(instance, request);
        }).catch(reject);
    });
  }

  /**
   * Test if there is a next page
   * @returns {boolean} - If there is a next page
   */
  get hasNext() {
    return this.page < this.pageCount;
  }

  /**
   * Test if there is a previous page
   * @returns {boolean} - If there is a previous page
   */
  get hasPrevious() {
    return this.page > 1;
  }

  /**
   * Get next page
   * @returns {Promise} - Resolves with {@link PaginatedResourceListing} instance and rejects with {@link OAuthError}
   */
  next() {
    return this.getPage(this.page + 1);
  }

  /**
   * Get previous page
   * @returns {Promise} - Resolves with {@link PaginatedResourceListing} instance and rejects with {@link OAuthError}
   */
  previous() {
    return this.getPage(this.page - 1);
  }
}

import {isParentOf} from './utils/reflection';
import ResourceBase from './crud/base/ResourceBase';
import PaginatedResourceListing from './PaginatedResourceListing';

/**
 * Proxy for accessing resource. This will make sure that they
 * are properly wrapped before the promise resolves.
 * @protected
 */
export default class ResourceProxy {
  /**
   * @param {Maps4News} api - Instance of the api
   * @param {ResourceBase} Target - Target to wrap
   */
  constructor(api, Target) {
    if (!isParentOf(ResourceBase, Target)) {
      throw new TypeError('Target is not a child of CrudBase');
    }

    if (typeof Target !== 'function') {
      throw new TypeError('Target must to be a class not an instance');
    }

    this._api = api;
    this._Target = Target;
  }

  /**
   * Get api instance
   * @returns {Maps4News} - Api instance
   */
  get api() {
    return this._api;
  }

  /**
   * Target to wrap results in
   * @returns {ResourceBase} - Target constructor
   * @constructor
   */
  get Target() {
    return this._Target;
  }

  /**
   * The name of the target
   * @returns {String} - Target name
   */
  get accessorName() {
    return this.Target.name.toLowerCase();
  }

  /**
   * Lists target resource
   * @param {Object<String, String|Array<String>>} query - Query
   * @param {Number} page - The page to be requested
   * @param {Number} perPage - Amount of items per page. This is silently capped by the API
   * @returns {Promise} - Resolves with {@link PaginatedResourceListing} instance and rejects with {@link OAuthError}
   *
   * @example
   * // Find layers with a name that starts with "test" and a scale_min between 1 and 10
   * // See Api documentation for search query syntax
   * var query = {
   *   name: '^:test',
   *   scale_min: ['>:1', '<:10'],
   * }
   *
   * api.layers.search(query).then(console.dir);
   */
  search(query, page = 1, perPage = null) {
    const url = this.new().baseUrl;
    const resolver = new PaginatedResourceListing(this._api, url, this.Target, query);

    return resolver.getPage(page, perPage);
  }

  /**
   * Lists target resource
   * @param {Number} page - The page to be requested
   * @param {Number} perPage - Amount of items per page. This is silently capped by the API
   * @returns {Promise} - Resolves with {@link PaginatedResourceListing} instance and rejects with {@link OAuthError}
   */
  list(page = 1, perPage = null) {
    return this.search({}, page, perPage);
  }

  /**
   * Get target resource
   * @param {Number|String} id - The resource id to be requested
   * @returns {Promise} - Resolves with {@link ResourceBase} instance and rejects with {@link OAuthError}
   */
  get(id) {
    const url = this.new({id: id}).url;

    return new Promise((resolve, reject) => {
      this._api
        .request(url)
        .catch(reject)
        .then(data => resolve(this.new(data)));
    });
  }

  /**
   * Select target resource without obtaining data
   * @param {Number|String} id - Resource id
   * @returns {ResourceBase} - Empty target resource
   * @example
   * api.users.select('me').colors().then(doSomethingCool);
   */
  select(id) {
    return this.new({id: id});
  }

  /**
   * Build a new isntance of the target
   * @param {Object<String, *>} data - Data for the object to be populated with
   * @returns {ResourceBase} - Resource with target data
   */
  new(data = {}) {
    return new this.Target(this._api, data);
  }
}

