import ResourceBase from './base/ResourceBase';

/**
 * Choropleth resource
 */
export default class Choropleth extends ResourceBase {
  get resourceName() {
    return 'choropleths';
  }
}

import CrudBase from './base/CrudBase';

/**
 * Color resource
 */
export default class Color extends CrudBase {
  get resourceName() {
    return 'colors';
  }

  get ownable() {
    return true;
  }
}

import CrudBase from './base/CrudBase';

/**
 * Contract resource
 */
export default class Contract extends CrudBase {
  get resourceName() {
    return 'contracts';
  }
}

import CrudBase from './base/CrudBase';

/**
 * Dimension resource
 */
export default class Dimension extends CrudBase {
  get resourceName() {
    return 'dimensions';
  }
}

import CrudSetBase from './base/CrudSetBase';
import Dimension from './Dimension';

/**
 * Dimension sets
 */
export default class DimensionSet extends CrudSetBase {
  get path() {
    return '/dimensions/sets/{id}';
  }

  get resourceName() {
    return 'dimension-sets';
  }

  get ownable() {
    return true;
  }

  get _Child() {
    return Dimension;
  }
}

import CrudBase from './base/CrudBase';

/**
