import CrudBase from './base/CrudBase';
import User from './User';
import Permission from './Permission';

export default class Group extends CrudBase {
  /**
   * Get a list of users in the group
   * @returns {Promise} - Resolves with {@link PaginatedResourceListing} instance containing {@link User} instances and rejects with {@link OAuthError}
   */
  users() {
    return this._listResource(User);
  }

  /**
   * Get the list of permissions granted to the group
   * @returns {Promise} - Resolves with {@link PaginatedResourceListing} instance containing {@link Permission} instances and rejects with {@link OAuthError}
   */
  permissions() {
    return this._listResource(Permission);
  }

  get resourceName() {
    return 'groups';
  }
}

import ResourceBase from './base/ResourceBase';

export default class Highlight extends ResourceBase {
  get resourceName() {
    return 'highlights';
  }
}

import ResourceBase from './base/ResourceBase';

export default class InsetMap extends ResourceBase {
  get resourceName() {
    return 'inset-maps';
  }
}

import CrudBase from './base/CrudBase';
import JobResult from './JobResult';
import JobRevision from './JobRevision';

export default class Job extends CrudBase {
  /**
   * Get the list of associated job results
   * @returns {Promise} - Resolves with {@link PaginatedResourceListing} instance containing {@link JobResult} instances and rejects with {@link OAuthError}
   */
  results() {
    return this._listResource(JobResult, `${this.url}/results`);
  }

  /**
   * Get the list job revisions
   * @returns {Promise} - Resolves with {@link PaginatedResourceListing} instance containing {@link JobRevision} instances and rejects with {@link OAuthError}
   */
  revisions() {
    return this._listResource(JobRevision, `${this.url}/revisions`);
  }

  /**
   * Get revision by id
   * @param {string} id - Revision id
   * @returns {Promise} -  Resolves with {@link JobRevision} instance and rejects with {@link OAuthError}
   */
  getRevision(id = 'last') {
    return new Promise((resolve, reject) => {
      this._api
        .request(`${this.path}/revisions/${id}`)
        .catch(reject)
        .then(data => resolve(new JobRevision(this._api, data)));
    });
  }

  get resourceName() {
    return 'jobs';
  }

