/**
 * Dummy flow for when the token should be available in the
 * cache and no attempt at authentication should be made.
 */
export default class DummyFlow extends OAuth {
  constructor() {
    super('', []);
  }

  /**
   * Authenticate
   * @returns {Promise} - Promise resolves with {@link OAuthToken} and rejects with {@link OAuthError}
   */
  authenticate() {
    return new Promise((resolve, reject) => {
      if (this.authenticated) {
        resolve(this.token);
      } else {
        reject(new OAuthError('dummy_error', 'Attempted authentication using a dummy authenticator'));
      }
    });
  }
}

import OAuth from './OAuth';
import OAuthToken from './OAuthToken';
import StateContainer from './StateContainer';
import {encodeQueryString} from '../utils/requests';
import OAuthError from './OAuthError';
import {isNode} from '../utils/node';

/**
 * Implicit OAuth flow using redirection
 */
export default class ImplicitFlow extends OAuth {

  /**
   * Implicit authentication flow
   * @param {String} clientId - OAuth client id
   * @param {String} callbackUrl - callbackUrl for obtaining the token. This should be a
   *                               page with this script on it. If left empty the current
   *                               url will be used.
   * @param {Array<String>} scopes - A list of required scopes
   * @param {Boolean} useState - use state verification
   * @returns {void}
   */
  constructor(clientId, callbackUrl = '', scopes = ['*'], useState = true) {
    super(clientId, scopes);

    if (isNode()) {
      throw new Error(`${this.constructor.name} can't be used under nodejs`);
    }

    this.path = '/oauth/authorize';

    this.callbackUrl = callbackUrl;
    this.useState = useState;

    if (!this.callbackUrl) {
      // Drop the anchor (if any)
      this.callbackUrl = window.location.toString().split('#')[0];
    }

    this._anchorParams = [
      'access_token', 'token_type', 'expires_in',
    ];

    if (this.useState) {
      this._anchorParams.push('state');
    }

    if (this._anchorContainsOAuthResponse()) {
      const anchorParams = this._getOAuthAnchorParams();

      this._cleanAnchorParams();

      if (this.useState && !StateContainer.validate(anchorParams['state'])) {
        throw Error('Invalid state in url');
      } else {
        this.token = OAuthToken.fromResponseObject(anchorParams);
      }
    }
  }

  /**
   * Authenticate
   * @returns {Promise} - Promise resolves with {@link OAuthToken} and rejects with {@link OAuthError}
   */
  authenticate() {
    return new Promise((resolve, reject) => {
      if (this.authenticated) {
        resolve(this.token);
      } else if (this._anchorContainsError()) {
        const err = this._getError();

        this._cleanAnchorParams();

        reject(err);
      } else {
        window.location = this._buildRedirectUrl();
      }
    });
  }

  /**
   * Builds the url for redirection
   * @returns {String} - Redirect url
   * @protected
   */
  _buildRedirectUrl() {
    const queryParams = {
      'client_id': this.clientId,
      'redirect_uri': this.callbackUrl,
      'response_type': 'token',
      'scope': this.scopes.join(' '),
    };

    if (this.useState) {
      queryParams['state'] = StateContainer.generate();
    }

    return `${this.host + this.path}?${encodeQueryString(queryParams)}`;
  }

  /**
   * Builds an object containing all the anchor parameters
   * @returns {Object<String, String>} - Anchor paramenters
   * @protected
   */
  _getAnchorParams() {
    const out = {};
    const query = window.location.hash.substr(1);

    for (const raw of query.split('&')) {
      const pair = raw.split('=').map(decodeURIComponent);

      out[pair[0]] = pair[1];
    }

    return out;
  }

  /**
   * Fetch OAuth anchor params
   * @param {Object<String, String>} query - Optional override for the query to analyse, defaults to {@link ImplicitFlow#_getAnchorParams}
   * @returns {Object<String, String>} - List of OAuth anchor parameters
   * @protected
   */
  _getOAuthAnchorParams(query = this._getAnchorParams()) {
    return Object.keys(query)
      .filter(key => this._anchorParams.includes(key))
      .reduce((obj, key) => {
        obj[key] = query[key];
        return obj;
      }, {});
  }

  /**
   * Remove OAuth related anchor parameters
   * @returns {void}
   * @protected
   */
  _cleanAnchorParams() {
    const anchorParams = this._getAnchorParams();
    const targets = this._anchorParams;

    // Just in case
    targets.push('state', 'error');

    for (const key of targets) {
      // Should silently fail when key doesn't exist
      delete anchorParams[key];
    }

    window.location.hash = encodeQueryString(anchorParams);
  }

  /**
   * Test if the anchor contains an OAuth response
   * @returns {Boolean} - if anchor tested positive for containing an OAuth response
   * @protected
   */
  _anchorContainsOAuthResponse() {
    const queryKeys = Object.keys(this._getOAuthAnchorParams());

    // Check if all the params are in the anchor
    return this._anchorParams.reduce((output, key) => {
      return output && queryKeys.includes(key);
    }, true);
  }

  /**
   * Test if the anchor contains an OAuth error
   * @returns {Boolean} - if anchor tested positive for containing an OAuth error
   * @protected
   */
  _anchorContainsError() {
    return Boolean(this._getAnchorParams()['error']);
  }

  /**
   * Get and return the error in the anchor
   * @returns {OAuthError} - OAuthError object
   * @protected
   */
  _getError() {
    if (!this._anchorContainsError()) {
      throw Error('No OAuthError found in anchor');
    }

    const params = this._getAnchorParams();

    return new OAuthError(
      params['error'], params['message']
    );
  }
}

import ImplicitFlow from './ImplicitFlow';
import OAuthToken from './OAuthToken';
import OAuthError from './OAuthError';

/**
 * Implicit OAuth flow using a pop-up.
 */
export default class ImplicitFlowPopup extends ImplicitFlow {
  /**
   * Implicit pop-up authentication flow
   * @param {String} clientId - OAuth client id
   * @param {String} callbackUrl - callbackUrl for obtaining the token. This should be a
   *                               page with this script on it. If left empty the current
   *                               url will be used.
   * @param {Array<String>} scopes - A list of required scopes
   * @param {Boolean} useState - use state verification
   * @param {String} windowOptions - optional window options for the pop-up window
   * @returns {void}
   */
  constructor(clientId, callbackUrl = '', scopes = ['*'], useState = false, windowOptions = 'width=800, height=600') {
    super(clientId, callbackUrl, scopes, useState);

    this.windowOptions = windowOptions;

    if (window.name === ImplicitFlowPopup.popupWindowName) {
      const data = this.token.toResponseObject() || this._getAnchorParams();

      localStorage.setItem(ImplicitFlowPopup.localStorageKey, JSON.stringify(data));

      window.close();
    }
  }

  /**
   * Popup window name
   * @returns {String} - window.name of the created pop-up
   * @constant
   */
  static get popupWindowName() {
    return 'm4n_api_auth';
  }

  /**
   * localStorage key name for temporarily storing the token
   * @returns {String} - key name
   * @constant
   */
  static get localStorageKey() {
    return 'm4n_api_auth_response';
  }

  /**
   * Authenticate
   * @returns {Promise} - Promise resolves with {@link OAuthToken} and rejects with {@link OAuthError}
   */
  authenticate() {
    if (window.name === ImplicitFlowPopup.popupWindowName) {
      return new Promise(() => {
      });
    }

    // Should be super.super.authenticate() :/
    if (this.authenticated) {
      return new Promise(resolve => {
        resolve(this.token);
      });
    }

    return new Promise((resolve, reject) => {
      const popup = window.open(
        this._buildRedirectUrl(),
        ImplicitFlowPopup.popupWindowName,
        this.windowOptions
      );

      const ticker = setInterval(() => {
        if (popup.closed) {
          clearInterval(ticker);

          const data = JSON.parse(localStorage.getItem(ImplicitFlowPopup.localStorageKey));

          localStorage.removeItem(ImplicitFlowPopup.localStorageKey);

          if (!data) {
            reject(new OAuthError('window_closed', 'Pop-up window was closed'));
          } else if (data.error) {
            reject(new OAuthError(data.error, data.message));
          } else {
            resolve(this.token = OAuthToken.fromResponseObject(data));
          }
        }
      }, 500);
    });
  }
}

import OAuthToken from './OAuthToken';
import {AbstractClassError, AbstractMethodError} from '../exceptions/AbstractError';

/**
 * OAuth base class
 * @abstract
 */
export default class OAuth {
  /**
   * @param {String} clientId - OAuth client id
   * @param {Array<String>} scopes - A list of required scopes
   * @returns {void}
   */
  constructor(clientId, scopes = ['*']) {
    if (this.constructor === OAuth) {
      throw new AbstractClassError();
    }

    this.clientId = String(clientId);
    this.scopes = scopes;
    this.token = OAuthToken.recover();
    this.host = 'https://api.Maps4News.com';
    this.path = '/';
  }

  /**
   * If the current instance has a valid token
   * @returns {Boolean} - if a valid token is availble
   */
  get authenticated() {
    return this.token !== null && !this.token.expired;
  }

  /**
   * Authenticate
   * @returns {Promise} - Promise resolves with OAuthToken and rejects with OAuthError
   * @abstract
   */
  authenticate() {
    throw new AbstractMethodError();
  }
}

/**
 * OAuth error
 */
export default class OAuthError extends Error {
  /**
   * OAuth error response
   * @param {String} error - OAuth error key
   * @param {String} message - OAuth error message
   */
  constructor(error, message = '') {
    super();
    this._error = String(error);
    this._message = String(message);
  }

  /**
   * OAuth error message
   * @returns {String} - message
   */
  get message() {
    return this._message;
  }

  /**
   * OAuth error code
   * @returns {String} - error
   */
  get error() {
    return this._error;
  }

  /**
   * Displayable error string
   * @returns {String} - error
   */
  toString() {
    let error = this._error;

    if (error.includes('_')) {
      error = error.replace('_', ' ').replace(/^./, x => x.toUpperCase());
    }

    if (this._message) {
      return `${error}: ${this._message}`;
    }
    return error;

  }
}

import {isNode} from '../utils/node';
import NodeError from '../exceptions/NodeError';

/**
 * Oauth token container
 */
export default class OAuthToken {
  /**
   * @param {String} token - OAuth token
   * @param {String} type - token type
   * @param {Date|Number} expires - expire time in seconds or Date
   * @param {Array<string>} scopes - Any scopes
   */
  constructor(token, type, expires, scopes = []) {
    this.scopes = scopes;
    this.token = token;
    this.type = type
      .toLowerCase()
      .replace(/(\s|^)\w/g, x => x.toUpperCase());

    if (typeof expires === 'number') {
      // Expires is in seconds
      this.expires = new Date(Date.now() + expires * 1000);
    } else if (expires instanceof Date) {
      this.expires = expires;
    } else {
      throw new TypeError('Expires not of type Date or Number');
    }
  }

  /**
   * String representation of the token, usable in the Authorization header
   * @returns {string} - String representation
   */
  toString() {
    return `${this.type} ${this.token}`;
  }

  /**
   * Get equivalent OAuth response object
   * @returns {{access_token: (String|*), token_type: String, expires_in: Number, scope: (Array.<String>|Array|*)}} - Raw response object
   */
  toResponseObject() {
    return {
      'access_token': this.token,
      'token_type': this.type.toLowerCase(),
      'expires_in': this.expires - Date.now(),
      'scope': this.scopes,
    };
  }

  /**
   * If the token has expired
   * @returns {Boolean} - expired
   */
  get expired() {
    return new Date() > this.expires;
  }

  /**
   * Internal storage key name
   * @returns {String} - storage name
   * @constant
   */
  static get storageName() {
    return 'm4n_api_token';
  }

  /**
   * Filename for nodejs token storage
   * @returns {string} - filename
   * @constant
   */
  static get nodeTokenFilename() {
    return '.m4n_token';
  }

  /**
   * Gets the default name for the name parameter on save and restore
   * @returns {string} - name
   * @private
   * @static
   */
  static get _defaultName() {
    return isNode() ? OAuthToken.nodeTokenFilename : OAuthToken.storageName;
  }

  /**
   * Build instance from response object
   * @param {String|Object} data - object or JSON string
   * @returns {OAuthToken} - New OAuthToken instance
   */
  static fromResponseObject(data) {
    if (typeof data === 'string') {
      data = JSON.parse(data);
    }

    return new OAuthToken(
      data['access_token'],
      data['token_type'],
      Number(data['expires_in']),
      data['scope'] || []
    );
  }

  /**
   * Store the token for later recovery. Token will be stored in HTTPS cookie if possible.
   * @param {String} name - db key name
   * @param {Boolean} forceLocalStorage - force the token to be stored in the localStorage
   * @returns {void}
   * @see OAuthToken#recover
   */
  save(name = OAuthToken._defaultName, forceLocalStorage = false) {
    const data = {
      token: this.token,
      type: this.type,
      expires: this.expires.toUTCString(),
    };

    if (isNode()) {
      if (forceLocalStorage) {
        throw new NodeError('Can not force localStorage usage when running under node');
      }

      // We're using eval to require fs to make sure that it isn't added to the bundle
      // eslint-disable-next-line no-eval
      const fs = eval('require("fs")');
      const json = JSON.stringify(data, null, 2);

      fs.writeFileSync(name, json);
    } else if (window.location.protocol === 'https:' && !forceLocalStorage) {
      const dataEncoded = encodeURIComponent(JSON.stringify(data));

      document.cookie = `${name}=${dataEncoded}; expires=${data.expires}`;
    } else {
      localStorage.setItem(name, JSON.stringify(data));
    }
  }

  /**
   * Recover a token by looking through the HTTPS cookies and localStorage
   * @param {String} name - Storage key name
   * @returns {OAuthToken|null} - null if none could be recovered
   * @see OAuthToken#save
   */
  static recover(name = OAuthToken._defaultName) {
    if (isNode()) {
      // We're using eval to require fs to make sure that it isn't added to the bundle
      // eslint-disable-next-line no-eval
      const fs = eval('require("fs")');
      const raw = fs.readFileSync(name);
      const data = JSON.parse(raw);

      return new OAuthToken(data.token, data.type, new Date(data.expires));
    }

    // Cookie
    if (window.location.protocol === 'https:') {
      const cookies = `; ${document.cookie}`;
      const parts = cookies.split(`; ${name}=`);

      if (parts.length === 2) {
        const raw = decodeURIComponent(parts[1].split(';')[0]);
        const data = JSON.parse(raw);

        return new OAuthToken(data.token, data.type, new Date(data.expires));
      }
    }

    // LocalStorage
    const raw = localStorage.getItem(name);

    if (raw) {
      const data = JSON.parse(raw);

      const instance = new OAuthToken(data.token, data.type, new Date(data.expires));

      if (!instance.expired) {
        return instance;
      }
    }

    return null;
  }
}

import OAuth from './OAuth';
import OAuthToken from './OAuthToken';
import {encodeQueryString, makeRequest} from '../utils/requests';
import OAuthError from './OAuthError';
import {isNode} from '../utils/node';

/**
 * Password authentication flow
 */
export default class PasswordFlow extends OAuth {
  /**
   * @param {string} clientId - OAuth client id
   * @param {string} secret - OAuth secret
   * @param {string} username - Valid username (email)
   * @param {string} password - Valid password
   * @param {Array<string>} scopes - A list of required scopes
   */
  constructor(clientId, secret, username, password, scopes = ['*']) {
    super(clientId, scopes);

    this._secret = secret;
    this._username = username;
    this._password = password;

    this._path = '/oauth/token';

    // Because the client requires a secret HTTPS is highly recommended
    if (!isNode() && window.location.protocol !== 'https:') {
      console.warn("Page was not loaded using https. You're probably leaking secrets right now");
    }
  }

  /**
   * it's a secret :o (client secret)
   * @returns {String} - secret
   */
  get secret() {
    return this._secret;
  }

  /**
   * Set client secret
   * @param {String} value - secret
   */
  set secret(value) {
    this._secret = value;
  }

  /**
   * Get the username for authentication
   * @returns {String} - Username (email)
   */
  get username() {
    return this._username;
  }

  /**
   * Get the username for authentication
   * @param {String} value - Username (email)
   */
  set username(value) {
    this._username = value;
  }

  /**
   * Get the password
   * @returns {String} - Password
   */
  get password() {
    return this._password;
  }

  /**
   * Set the password
   * @param {String} value - password
   */
  set password(value) {
    this._password = value;
  }

  /**
   * OAuth path
   * @returns {String} - OAuth path
   */
  get path() {
    return this._path;
  }

  /**
   * OAuth path
   * @param {String} value - OAuth path
   */
  set path(value) {
    this._path = value;
  }

  /**
   * Authenticate
   * @returns {Promise} - Promise resolves with {@link OAuthToken} and rejects with {@link OAuthError}
   */
  authenticate() {
    const url = this.host + this._path;
    const query = {
      'grant_type': '_password',
      'client_id': this.clientId,
      'client_secret': this._secret,
      'username': this._username,
      'password': this._password,
      'scope': this.scopes.join(' '),
    };

    return new Promise((resolve, reject) => {
      makeRequest(url, 'POST', encodeQueryString(query)).then(request => {
        const data = JSON.parse(request.responseText);

        this.token = OAuthToken.fromResponseObject(data);
        this.token.scopes = this.scopes;
        resolve(this.token);
      }).catch(request => {
        const data = JSON.parse(request.responseText);

        reject(new OAuthError(data['error'], data['message']));
      });
    });
  }
}

import StaticClass from '../utils/StaticClass';
import Uuid from '../utils/uuid';

/**
 * State container for keeping track of OAuth states (crsf tokens)
 * @static
 * @private
 */
export default class StateContainer extends StaticClass {
  /**
   * LocalStorage key prefix
   * @returns {String} - prefix
   * @constant
   */
  static get prefix() {
    return 'm4n_api_state_';
  }

  /**
   * Generate and store a state that can be checked at a later point
   * @returns {string} - state
   */
  static generate() {
    const key = StateContainer.prefix + Date.now();
    const value = Uuid.uuid4();

    localStorage.setItem(key, value);
    return value;
  }

  /**
   * Validate a state
   * @param {String} state - state to validate
   * @param {Boolean} purge - remove from state db after validation
   * @returns {Boolean} - if the state is valid
   */
  static validate(state, purge = true) {
    for (let i = 0; i < localStorage.length; i++) {
      const key = localStorage.key(i);

      if (localStorage.getItem(key) === state) {
        if (purge) {
          localStorage.removeItem(key);
        }

        return true;
      }
    }

    return false;
  }

  /**
   * Remove all states from the state db
   * @returns {void}
   */
  static clean() {
    for (let i = 0; i < localStorage.length; i++) {
      const key = localStorage.key(i);
      const prefix = key.slice(0, StateContainer.prefix.length);

      if (prefix === StateContainer.prefix) {
        localStorage.removeItem(key);
      }
    }
  }

  /**
   * Get states with their corresponding state db key
   * @returns {Object<String, String>} - List of stored states
   */
  static list() {
    const out = {};

    for (let i = 0; i < localStorage.length; i++) {
      const key = localStorage.key(i);
      const prefix = key.slice(0, StateContainer.prefix.length);

      if (prefix === StateContainer.prefix) {
        out[key] = localStorage.getItem(key);
      }
    }

    return out;
  }
}

import StaticClassError from '../exceptions/StaticClassError';

/**
 * Disables the constructor and throws a {@link StaticClassError} when an instance is created.
 * @protected
 */
export default class StaticClass {
  constructor() {
    throw new StaticClassError();
  }
}

/**
 * Converts snake_case strings to camelCase
 * @param {String} str - a snake_case string
 * @returns {String} - Converted camelCase string
 * @private
 */
export function snakeToCamelCase(str) {
  return str.replace(/(?:(_[a-z\d]))/g, x => x[1].toUpperCase());
}

/**
 * Converts camelCase strings to snake_case
 * @param {String} str - a camelCase string
 * @returns {String} - Converted snake_case string
 * @private
 */
export function camelToSnakeCase(str) {
  return str.replace(/([A-Z])/g, x => '_' + x.toLowerCase());
}

/**
 * Test if the application is running under nodejs
 * @returns {boolean} - Is the application running under node?
 * @see https://nodejs.org
 * @private
 */
export function isNode() {
  // eslint-disable-next-line no-undef
  return typeof window === 'undefined' && typeof module !== 'undefined' && module.exports;
}

/**
 * Tests if the parent is a parent of child
 * @param {function|object} parent - Instance or Class
 * @param {function|object} child - Instance or Class
 * @returns {boolean} - is parent a parent of child
 * @private
 * @example
 * class A {}
 * class B extends A {}
 * class C extends B {}
 *
 * isParentOf(A, C) // true
 * isParentOf(B, C) // true
 * isParentOf(C, C) // true
 * isParentOf(B, A) // false
 */
export function isParentOf(parent, child) {
  parent = typeof parent === 'function' ? parent : parent.constructor;
  child = typeof child === 'function' ? child : child.constructor;

  do {
    if (child.name === parent.name) {
      return true;
    }

    child = Object.getPrototypeOf(child);
  } while (child.name !== '');

  return false;
}

